#!/bin/bash

# VexFS VM Creation Script for Kernel Module Testing
# Creates a lightweight Ubuntu VM specifically for kernel module testing

set -e

SCRIPT_DIR="$(cd "$(dirname "${BASH_SOURCE[0]}")" && pwd)"
PROJECT_ROOT="$(cd "$SCRIPT_DIR/../.." && pwd)"
VM_DIR="$SCRIPT_DIR/vm"
LOGS_DIR="$VM_DIR/logs"

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

log() {
    echo -e "${BLUE}[$(date '+%Y-%m-%d %H:%M:%S')]${NC} $1"
}

warn() {
    echo -e "${YELLOW}[WARNING]${NC} $1"
}

error() {
    echo -e "${RED}[ERROR]${NC} $1"
}

success() {
    echo -e "${GREEN}[SUCCESS]${NC} $1"
}

# VM Configuration
VM_NAME="vexfs-test-vm"
VM_MEMORY="2G"
VM_CORES="2"
VM_DISK_SIZE="10G"
VM_IMAGE="$VM_DIR/vexfs-test.qcow2"

# Check dependencies
log "Checking dependencies..."
if ! command -v qemu-system-x86_64 &> /dev/null; then
    error "QEMU not found. Please install qemu-system-x86_64"
    exit 1
fi

if ! command -v wget &> /dev/null; then
    error "wget not found. Please install wget"
    exit 1
fi

# Create directories
mkdir -p "$VM_DIR"
mkdir -p "$LOGS_DIR"

# Check if VM already exists
if [[ -f "$VM_IMAGE" ]]; then
    log "VM image already exists at $VM_IMAGE"
    read -p "Do you want to recreate it? (y/N): " -n 1 -r
    echo
    if [[ $REPLY =~ ^[Yy]$ ]]; then
        rm -f "$VM_IMAGE"
        log "Removed existing VM image"
    else
        log "Using existing VM image"
        exit 0
    fi
fi

# Download Ubuntu Server ISO if not present
UBUNTU_ISO="$VM_DIR/ubuntu-22.04-server.iso"
if [[ ! -f "$UBUNTU_ISO" ]]; then
    log "Downloading Ubuntu Server 22.04 ISO..."
    wget -O "$UBUNTU_ISO" \
        "https://releases.ubuntu.com/22.04/ubuntu-22.04.3-live-server-amd64.iso"
    success "Ubuntu ISO downloaded"
else
    log "Ubuntu ISO already exists"
fi

# Create VM disk
log "Creating VM disk image..."
qemu-img create -f qcow2 "$VM_IMAGE" $VM_DISK_SIZE
success "VM disk created: $VM_IMAGE"

# Create cloud-init configuration for automated installation
log "Creating cloud-init configuration..."
cat > "$VM_DIR/user-data" << 'EOF'
#cloud-config
autoinstall:
  version: 1
  locale: en_US.UTF-8
  keyboard:
    layout: us
  network:
    network:
      version: 2
      ethernets:
        enp0s3:
          dhcp4: true
  storage:
    layout:
      name: direct
  identity:
    hostname: vexfs-test
    username: vexfs
    password: '$6$rounds=4096$saltsalt$L9.LKkHtJWWW7aBQqDP.